mapper.select:
SELECT CAST(COUNT(*) AS INTEGER) AS CNT
FROM SALES_FILE_MAP
WHERE SOURCE_CD = #{SOURCE_CD}
AND  SOURCE_SEQ = #{SOURCE_SEQ}
AND  STATUS_CD = #{STATUS_CD}

SELECT CAST(COUNT(*) AS INTEGER) AS CNT
FROM SALES_FILE_MAP
WHERE FM_SEQ = #{FM_SEQ}
AND SOURCE_CD = #{SOURCE_CD}
AND  SOURCE_SEQ = #{SOURCE_SEQ}

SELECT CAST(COUNT(*) AS INTEGER) AS CNT
FROM SALES_FILE_MASTER
WHERE FM_SEQ = #{FM_SEQ}

mapper.insert:
INSERT INTO SALES_FILE_MAP
(
SOURCE_CD
, SOURCE_SEQ
, STATUS_CD
, FM_SEQ
, DEL_FLAG
, SHARE_FLAG
, INPT_ID
, INPT_IP
, INPT_DTTM
, CHGE_ID
, CHGE_IP
, CHGE_DTTM
)VALUES(
 #{SOURCE_CD}
,#{SOURCE_SEQ}
,#{STATUS_CD}
,#{FM_SEQ}
,#{DEL_FLAG}
,#{SHARE_FLAG}
,#{USER_ID_SRV}
,#{USER_CON_IPADDR_SRV}
,NOW()
,#{USER_ID_SRV}
,#{USER_CON_IPADDR_SRV}
,NOW()
)
ON 
DUPLICATE KEY
	UPDATE 
	      FM_SEQ = #{FM_SEQ}

mapper.delete:
DELETE FROM SALES_FILE_MAP
WHERE SOURCE_CD=#{SOURCE_CD} 
AND SOURCE_SEQ=#{SOURCE_SEQ} 
AND STATUS_CD=#{STATUS_CD}

DELETE FROM SALES_FILE_MAP
WHERE SOURCE_CD=#{SOURCE_CD} 
AND SOURCE_SEQ=#{SOURCE_SEQ} 
AND STATUS_CD=#{STATUS_CD}
AND FM_SEQ=#{FM_SEQ}

mapper.insert:
INSERT INTO SALES_FILE_MASTER
(
 FM_SEQ
, FILE_SEQ
, SAVE_FILE_NAME
, FILE_NAME
, FILE_SIZE
, FILE_TYPE
, SAVE_PATH
, INPT_ID
, INPT_IP
, INPT_DTTM
, CHGE_ID
, CHGE_IP
, CHGE_DTTM
)VALUES(
 #{FM_SEQ}
,(SELECT COALESCE(MAX(FILE_SEQ),0)+1 FROM SALES_FILE_MASTER F WHERE FM_SEQ = #{FM_SEQ})
,#{SAVE_FILE_NAME}
,#{FILE_NAME}
,#{FILE_SIZE}
,#{FILE_TYPE}
,#{SAVE_PATH}
,#{USER_ID_SRV}
,#{USER_CON_IPADDR_SRV}
,NOW()
,#{USER_ID_SRV}
,#{USER_CON_IPADDR_SRV}
,NOW()
)
ON 
DUPLICATE KEY
	UPDATE 
	    SAVE_FILE_NAME 			= #{SAVE_FILE_NAME}
	    , FILE_NAME 			= #{FILE_NAME}
	    , FILE_SIZE 		= #{FILE_SIZE}
	    , FILE_TYPE 		= #{FILE_TYPE}
	    , SAVE_PATH 		= #{SAVE_PATH}
	    , CHGE_ID 			= #{USER_ID_SRV}
	    , CHGE_IP			= #{USER_CON_IPADDR_SRV}
	    , CHGE_DTTM			= NOW()

mapper.delete:
DELETE FROM SALES_FILE_MASTER
WHERE FM_SEQ=#{FM_SEQ} 
AND FILE_SEQ=#{FILE_SEQ}
