-- insertSP_Contract_R02 -- 매출 계약 INSERT INTO SALES_CONTRACT_LIST ( CONTRACT_NO , PROJECT_CODE , CO_CD , CONTRACT_TYPE , BUSINESS_TYPE , CONTRACT_NAME , PRODUCT_CODE , CONTRACT_DATE , SALES_EMP_NO , SALES_CO_CD , SALES_DEPT_CD , SALES_DEPT_NAME , INCENTIVE_EMP_NO , CONTRACT_STATUS , SALES_STATUS , PIPELINE_FLAG , CONTRACT_MAIN , SITECHK_FLAG , EXPORT_FLAG , PAY_METHOD , PREPAYMENT_EXECUTION_RATE , CONTRACT_EXECUTION_RATE , DEFECT_EXECUTION_RATE , PROGRESS_EXCEPT_FLAG , PROGRESS_100_FLAG , PROGRESS_ADD_FLAG , BAD_DEBT_FLAG , BAD_DEBT_DATE , COLLECTION_BOND_FLAG , NET_SALES_FLAG , DIVISION_FLAG , CLIENT_PO_NO , REMARKS , CONFIRM_EMP_NO , CONFIRM_DATE , DELETE_FLAG ,INPT_ID ,INPT_IP ,INPT_DTTM ,CHGE_ID ,CHGE_IP ,CHGE_DTTM ) SELECT #{NEW_CONTRACT_NO} , PROJECT_CODE , CO_CD , CONTRACT_TYPE , BUSINESS_TYPE , CONTRACT_NAME , PRODUCT_CODE , #{CONTRACT_DATE} , SALES_EMP_NO , SALES_CO_CD , SALES_DEPT_CD , SALES_DEPT_NAME , INCENTIVE_EMP_NO , CONTRACT_STATUS , SALES_STATUS , PIPELINE_FLAG , CONTRACT_MAIN , SITECHK_FLAG , EXPORT_FLAG , PAY_METHOD , PREPAYMENT_EXECUTION_RATE , CONTRACT_EXECUTION_RATE , DEFECT_EXECUTION_RATE , PROGRESS_EXCEPT_FLAG , PROGRESS_100_FLAG , PROGRESS_ADD_FLAG , BAD_DEBT_FLAG , BAD_DEBT_DATE , COLLECTION_BOND_FLAG , NET_SALES_FLAG , DIVISION_FLAG , CLIENT_PO_NO , REMARKS , #{EMP_NO_SRV} , NOW() , DELETE_FLAG ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() FROM SALES_CONTRACT_LIST WHERE CONTRACT_NO = #{CONTRACT_NO} AND DELETE_FLAG = '0';
-- 유지보수계약 INSERT INTO SALES_CONTRACT_MA ( CONTRACT_NO , START_DATE , END_DATE , UNION_FLAG , AUTO_EXTENSION_FLAG , REGULAR_VISIT , ATTACH , PUBLISHER , VIST_CYCLE , REQUEST_CYCLE , REQUEST_PRICE , REQUEST_MONTH , REQUEST_DAY , RESULT_DATE , AUTO_ISSUE_FLAG ,INPT_ID ,INPT_IP ,INPT_DTTM ,CHGE_ID ,CHGE_IP ,CHGE_DTTM ) SELECT #{NEW_CONTRACT_NO} , DATE_ADD(START_DATE, INTERVAL 1 YEAR) AS START_DATE , GET_MA_END_DATE(DATE_ADD(END_DATE, INTERVAL 1 YEAR)) AS END_DATE , UNION_FLAG , AUTO_EXTENSION_FLAG , REGULAR_VISIT , ATTACH , PUBLISHER , VIST_CYCLE , REQUEST_CYCLE , REQUEST_PRICE , REQUEST_MONTH , REQUEST_DAY , #{CONTRACT_DATE} , IFNULL(AUTO_ISSUE_FLAG, '0') ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() FROM SALES_CONTRACT_MA WHERE CONTRACT_NO = #{CONTRACT_NO};
-- 계약 이력 INSERT INTO SALES_CONTRACT_MA_HISTORY ( CONTRACT_NO , REGULAR_VISIT , VIST_CYCLE ,INPT_ID ,INPT_IP ,INPT_DTTM ,CHGE_ID ,CHGE_IP ,CHGE_DTTM ) SELECT #{NEW_CONTRACT_NO} AS CONTRACT_NO , REGULAR_VISIT , VIST_CYCLE ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() FROM SALES_CONTRACT_MA WHERE CONTRACT_NO = #{CONTRACT_NO};
-- 유지보수계약 상세 INSERT INTO SALES_CONTRACT_MA_DETAIL ( CONTRACT_NO , PRODUCT_CODE , MA_PRODUCT_CODE , START_DATE , END_DATE , CONTRACT_PRICE , MA_RATE , MA_STANDARD , REMARKS , DELETE_FLAG , ORD , PURCHASE_EXCEPT_FLAG ,INPT_ID ,INPT_IP ,INPT_DTTM ,CHGE_ID ,CHGE_IP ,CHGE_DTTM ) SELECT #{NEW_CONTRACT_NO} , PRODUCT_CODE , MA_PRODUCT_CODE , DATE_ADD(START_DATE, INTERVAL 1 YEAR) AS START_DATE , GET_MA_END_DATE(DATE_ADD(END_DATE, INTERVAL 1 YEAR)) AS END_DATE , CONTRACT_PRICE , MA_RATE , MA_STANDARD , REMARKS , DELETE_FLAG , ORD , '0' ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() FROM SALES_CONTRACT_MA_DETAIL WHERE CONTRACT_NO = #{CONTRACT_NO} AND DELETE_FLAG = '0';
-- 통합유지보수 INSERT INTO SALES_CONTRACT_MA_UNION ( CONTRACT_NO , UNION_CONTRACT_NO ,INPT_ID ,INPT_IP ,INPT_DTTM ,CHGE_ID ,CHGE_IP ,CHGE_DTTM ) SELECT #{NEW_CONTRACT_NO} , UNION_CONTRACT_NO ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() FROM SALES_CONTRACT_MA_UNION WHERE CONTRACT_NO = #{CONTRACT_NO};
-- 계약 제품 업데이트 UPDATE SALES_CONTRACT_PRODUCT SET SUPPORT_STATUS = '006' , SUPPORT_STOP_START_DATE = NULL , SUPPORT_STOP_END_DATE = NULL , SUPPORT_REMARKS = NULL , SUPPORT_STATUS_UPDATE_DATE = NOW() , SUPPORT_STATUS_UPDATE_EMP_NO = #{EMP_NO_SRV} , CHGE_ID = #{EMP_NO_SRV} , CHGE_IP = #{USER_CON_IPADDR_SRV} , CHGE_DTTM = NOW() WHERE CONTRACT_NO IN ( SELECT UNION_CONTRACT_NO FROM SALES_CONTRACT_MA_UNION WHERE CONTRACT_NO = #{CONTRACT_NO} );
-- 지원 상태 이력 추가 INSERT INTO SALES_SUPPORT_STATUS_HISTORY ( CONTRACT_NO , SUPPORT_STATUS ,INPT_ID ,INPT_IP ,INPT_DTTM ,CHGE_ID ,CHGE_IP ,CHGE_DTTM ) SELECT UNION_CONTRACT_NO , '006' ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() FROM SALES_CONTRACT_MA_UNION WHERE CONTRACT_NO = #{CONTRACT_NO};
