, #{CSD_SEQ}, #{START_DATE}, #{END_DATE}, #{MEN_MONTH}, #{UNIT_COST}, #{CONTRACT_PRICE}, #{REMARKS} ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() ,#{EMP_NO_SRV} ,#{USER_CON_IPADDR_SRV} ,NOW() );

